SELECT "topics".* FROM "topics" LEFT JOIN topic_users tu ON topics.id = tu.topic_id AND tu.user_id = 7602 LEFT JOIN topic_allowed_users ta ON topics.id = ta.topic_id AND ta.user_id = 7602 WHERE ("topics"."deleted_at" IS NOT NULL) AND (topics.archetype = 'private_message') AND (ta.topic_id IS NOT NULL) AND (tu.last_read_post_number < topics.highest_post_number) AND (COALESCE(tu.notification_level, 1) >= 2) AND (topics.id NOT IN (19120,19121)) AND "topics"."visible" = TRUE ORDER BY topics.bumped_at DESC LIMIT 4