SELECT "topics".* FROM "topics" LEFT JOIN topic_users tu ON topics.id = tu.topic_id AND tu.user_id = 7608 LEFT JOIN ( SELECT * FROM topic_allowed_groups _tg LEFT JOIN group_users gu ON gu.user_id = 7608 AND gu.group_id = _tg.group_id WHERE gu.group_id IN (2953) ) tg ON topics.id = tg.topic_id WHERE ("topics"."deleted_at" IS NOT NULL) AND (topics.archetype = 'private_message') AND (tg.topic_id IS NOT NULL) AND (tu.last_read_post_number < topics.highest_post_number) AND (COALESCE(tu.notification_level, 1) >= 2) AND (topics.id NOT IN (19123,19125,19124)) AND "topics"."visible" = TRUE ORDER BY topics.bumped_at DESC LIMIT 3