SELECT "users".* FROM "users" INNER JOIN "user_stats" ON "user_stats"."user_id" = "users"."id" WHERE (users.id > 0) AND (NOT EXISTS( SELECT 1 FROM anonymous_users a WHERE a.user_id = users.id )) AND (suspended_till IS NULL OR suspended_till <= '2020-03-26 06:26:39.091850') AND "users"."trust_level" = 2 AND "users"."manual_locked_trust_level" IS NULL AND 1=1 AND (user_stats.days_visited >= 50) AND (user_stats.topic_reply_count >= 10) AND (user_stats.topics_entered >= 200) AND (user_stats.posts_read_count >= 500) AND (user_stats.likes_given >= 30) AND (user_stats.likes_received >= 20) ORDER BY "users"."id" ASC LIMIT 1000