SELECT "theme_fields".* FROM "theme_fields" JOIN ( SELECT 1235 AS theme_id, 0 AS theme_sort_column UNION ALL SELECT 1230 AS theme_id, 1 AS theme_sort_column UNION ALL SELECT 1231 AS theme_id, 2 AS theme_sort_column ) as X ON X.theme_id = theme_fields.theme_id WHERE "theme_fields"."theme_id" IN (1235, 1230, 1231) AND "theme_fields"."type_id" = 2