SELECT "topics"."id" FROM "topics" LEFT OUTER JOIN "categories" ON "categories"."id" = "topics"."category_id" LEFT OUTER JOIN topic_users AS tu ON (topics.id = tu.topic_id AND tu.user_id = -1) WHERE (categories.id in (3884) AND ( categories.topic_id <> topics.id OR categories.id = 3884 ) ) AND (topics.archetype <> 'private_message') AND (COALESCE(categories.topic_id, 0) <> topics.id) AND "topics"."visible" = TRUE AND "topics"."id" != 19601 AND (topics.deleted_at IS NOT NULL) AND (topics.pinned_at IS NOT NULL OR topics.category_id <> 3884) ORDER BY topics.bumped_at DESC LIMIT 3