SELECT "topics"."id" FROM "topics" LEFT OUTER JOIN "categories" ON "categories"."id" = "topics"."category_id" WHERE (categories.id in (3884) AND ( categories.topic_id <> topics.id OR categories.id = 3884 ) ) AND (topics.archetype <> 'private_message') AND (COALESCE(categories.topic_id, 0) <> topics.id) AND "topics"."visible" = TRUE AND "topics"."id" NOT IN (19601, 19602) AND (topics.deleted_at IS NOT NULL) AND (topics.category_id IS NULL or topics.category_id IN (1,3884)) AND (topics.pinned_at IS NOT NULL OR topics.category_id <> 3884) ORDER BY topics.bumped_at DESC LIMIT 3