SELECT "topics".* FROM "topics" LEFT JOIN topic_users tu ON topics.id = tu.topic_id AND tu.user_id = 13455 LEFT JOIN topic_allowed_users ta ON topics.id = ta.topic_id AND ta.user_id = 13455 LEFT JOIN topic_allowed_users ta2 ON topics.id = ta2.topic_id AND ta2.user_id IN (2, 1995, 8307, 17621, 22980,-10) LEFT JOIN topic_allowed_groups tg ON topics.id = tg.topic_id AND tg.group_id IN (-10) WHERE ("topics"."deleted_at" IS NOT NULL) AND ( topics.archetype = 'private_message' ) AND (ta.topic_id IS NOT NULL) AND ( ta2.topic_id IS NOT NULL OR tg.topic_id IS NOT NULL ) AND ( topics.id NOT IN (68559, 60069, 42145) ) AND "topics"."visible" = 't' ORDER BY topics.bumped_at DESC LIMIT 5;