SELECT COUNT(*) FROM "groups" LEFT JOIN group_users gu ON gu.group_id = groups.id AND gu.user_id = 783 AND gu.owner WHERE (groups.id > 0) AND ( groups.visibility_level IN (0, 1, 2, 4) OR ( groups.visibility_level = 3 AND gu.id IS NOT NULL ) ) AND ( groups.members_visibility_level IN (0, 1, 2, 4) OR ( groups.visibility_level = 3 AND gu.id IS NOT NULL ) ) AND (gu.user_id = 780) AND ( automatic IS FALSE OR groups.id = 2 ) 