SELECT "tags"."id" AS t0_r0, "tags"."name" AS t0_r1, "tags"."topic_count" AS t0_r2, "tags"."created_at" AS t0_r3, "tags"."updated_at" AS t0_r4, "tags"."pm_topic_count" AS t0_r5, "tags"."target_tag_id" AS t0_r6, "target_tags_tags"."id" AS t1_r0, "target_tags_tags"."name" AS t1_r1, "target_tags_tags"."topic_count" AS t1_r2, "target_tags_tags"."created_at" AS t1_r3, "target_tags_tags"."updated_at" AS t1_r4, "target_tags_tags"."pm_topic_count" AS t1_r5, "target_tags_tags"."target_tag_id" AS t1_r6 FROM "tags" INNER JOIN "tags" "target_tags_tags" ON "target_tags_tags"."id" = "tags"."target_tag_id" WHERE (lower(tags.name) IN ('tag1','tag2'))