SELECT "groups"."id", "groups"."name" FROM "groups" INNER JOIN "group_users" ON "groups"."id" = "group_users"."group_id" WHERE "group_users"."user_id" = 296 AND (groups.id > 0) AND ( groups.id IN ( SELECT id FROM groups WHERE visibility_level = 0 UNION ALL SELECT id FROM groups WHERE visibility_level = 1 AND 296 IS NOT NULL UNION ALL SELECT g.id FROM groups g JOIN group_users gu ON gu.group_id = g.id AND gu.user_id = 296 WHERE g.visibility_level = 2 UNION ALL SELECT g.id FROM groups g LEFT JOIN group_users gu ON gu.group_id = g.id AND gu.user_id = 296 AND gu.owner WHERE g.visibility_level = 3 AND (gu.id IS NOT NULL OR FALSE) UNION ALL SELECT g.id FROM groups g JOIN group_users gu ON gu.group_id = g.id AND gu.user_id = 296 AND gu.owner WHERE g.visibility_level = 4 ) ) ORDER BY name ASC