SELECT "topics".* FROM "topics" LEFT OUTER JOIN topic_users ON topic_users.topic_id = topics.id AND topic_users.user_id = 469 LEFT OUTER JOIN category_users ON category_users.category_id = topics.category_id AND category_users.user_id = 469 LEFT OUTER JOIN users ON users.id = topics.user_id LEFT OUTER JOIN top_topics ON top_topics.topic_id = topics.id WHERE ("topics"."deleted_at" IS NOT NULL) AND "topics"."visible" = TRUE AND (topics.category_id IS NULL OR topics.category_id IN (SELECT id FROM categories WHERE NOT read_restricted)) AND "topics"."closed" = FALSE AND "topics"."archived" = FALSE AND (COALESCE(topic_users.notification_level, 1) <> 0) AND (topics.created_at > '2020-02-26 06:15:04.075390') AND (topics.created_at < '2020-03-26 06:10:04.081679') AND (topics.archetype <> 'private_message') AND (COALESCE(users.trust_level, 0) > 0) ORDER BY COALESCE(topic_users.notification_level, 1) DESC, COALESCE(category_users.notification_level, 1) DESC, COALESCE(top_topics.yearly_score, 0) DESC, topics.bumped_at DESC LIMIT 10