SELECT "users".* FROM "users" INNER JOIN "user_options" ON "user_options"."user_id" = "users"."id" WHERE "users"."active" = TRUE AND (silenced_till IS NULL OR silenced_till <= '2020-03-26 06:15:22.768559') AND (suspended_till IS NULL OR suspended_till <= '2020-03-26 06:15:22.768650') AND (users.id > 0) AND (NOT EXISTS( SELECT 1 FROM anonymous_users a WHERE a.user_id = users.id )) AND (user_options.mailing_list_mode AND user_options.mailing_list_mode_frequency > 0) AND (NOT EXISTS ( SELECT 1 FROM muted_users mu WHERE mu.muted_user_id = 737 AND mu.user_id = users.id )) AND (NOT EXISTS ( SELECT 1 FROM ignored_users iu WHERE iu.ignored_user_id = 737 AND iu.user_id = users.id )) AND (NOT EXISTS ( SELECT 1 FROM topic_users tu WHERE tu.topic_id = 15951 AND tu.user_id = users.id AND tu.notification_level = 0 )) AND (NOT EXISTS ( SELECT 1 FROM category_users cu WHERE cu.category_id = 1 AND cu.user_id = users.id AND cu.notification_level = 0 )) AND (NOT EXISTS ( SELECT 1 FROM tag_users tu WHERE tu.tag_id in (1644) AND tu.user_id = users.id AND tu.notification_level = 0 )) AND "users"."approved" = TRUE ORDER BY "users"."id" ASC LIMIT 1000