SELECT DISTINCT posts.created_at AS alias_0, "posts"."id" FROM "posts" INNER JOIN "group_mentions" ON "group_mentions"."post_id" = "posts"."id" LEFT OUTER JOIN "users" ON "users"."id" = "posts"."user_id" LEFT OUTER JOIN "topics" ON "topics"."id" = "posts"."topic_id" AND ("topics"."deleted_at" IS NOT NULL) LEFT OUTER JOIN "categories" ON "categories"."id" = "topics"."category_id" WHERE ("posts"."deleted_at" IS NOT NULL) AND (topics.archetype <> 'private_message') AND "posts"."post_type" = 1 AND (group_mentions.group_id = 2411) AND (topics.category_id IS NULL or topics.category_id IN (1)) ORDER BY posts.created_at desc LIMIT 50