SELECT "topics"."id", "topics"."user_id", "topics"."node_id", "topics"."title", "topics"."last_reply_id", "topics"."last_reply_user_id", "topics"."last_reply_user_login", "topics"."node_name", "topics"."last_active_mark", "topics"."lock_node", "topics"."suggested_at", "topics"."grade", "topics"."replied_at", "topics"."replies_count", "topics"."likes_count", "topics"."mentioned_user_ids", "topics"."deleted_at", "topics"."created_at", "topics"."updated_at", "topics"."closed_at", "topics"."team_id" FROM "topics" WHERE "topics"."deleted_at" IS NULL AND "topics"."node_id" != $1 AND "topics"."grade" != $2 AND (likes_count > 5) ORDER BY "topics"."last_active_mark" DESC LIMIT $3 OFFSET $4