SELECT "users"."type", "users"."id", "users"."name", "users"."login", "users"."email", "users"."email_md5", "users"."email_public", "users"."avatar", "users"."state", "users"."tagline", "users"."github", "users"."website", "users"."location", "users"."location_id", "users"."twitter", "users"."team_users_count", "users"."created_at", "users"."updated_at" FROM "users" INNER JOIN "actions" ON "users"."id" = "actions"."user_id" WHERE "actions"."target_id" = $1 AND "actions"."action_type" = $2 AND "actions"."target_type" = $3 AND "actions"."user_type" = $4 AND "actions"."user_type" = $5 ORDER BY actions.id asc LIMIT $6 OFFSET $7