SELECT "users"."type", "users"."id", "users"."name", "users"."login", "users"."email", "users"."email_md5", "users"."email_public", "users"."avatar", "users"."state", "users"."tagline", "users"."github", "users"."website", "users"."location", "users"."location_id", "users"."twitter", "users"."team_users_count", "users"."created_at", "users"."updated_at" FROM "users" WHERE "users"."location_id" = $1 AND "users"."type" IS NULL ORDER BY "users"."replies_count" DESC LIMIT $2 OFFSET $3