 select stddev(sum_downvotes) as stddev, sum(sum_downvotes) as sum, avg(sum_downvotes) as avg, avg(n_comments) as n_comments, count(*) as n_commenters from ( select sum(downvotes) as sum_downvotes, count(*) as n_comments from comments join users on comments.user_id = users.id where (comments.created_at >= '2020-04-16 10:39:12 -0500') and users.banned_at is null and users.deleted_at is null GROUP BY comments.user_id ) sums; 