SELECT COUNT(DISTINCT stories.id) FROM `stories` INNER JOIN `domains` ON `domains`.`id` = `stories`.`domain_id` LEFT OUTER JOIN `taggings` ON `taggings`.`story_id` = `stories`.`id` LEFT OUTER JOIN `tags` ON `tags`.`id` = `taggings`.`tag_id` LEFT OUTER JOIN `users` ON `users`.`id` = `stories`.`user_id` WHERE `stories`.`merged_story_id` IS NULL AND `stories`.`is_expired` = FALSE AND (domains.domain = 'lobste.rs') AND (((stories.title) LIKE '%term1%' OR (stories.description) LIKE '%term1%' OR (stories.story_cache) LIKE '%term1%'))