SELECT COUNT(stories.id) AS count_stories_id, stories.id AS stories_id FROM `stories` INNER JOIN `taggings` ON `taggings`.`story_id` = `stories`.`id` INNER JOIN `tags` ON `tags`.`id` = `taggings`.`tag_id` INNER JOIN `users` ON `users`.`id` = `stories`.`user_id` WHERE `stories`.`merged_story_id` IS NULL AND `stories`.`is_expired` = FALSE AND (((stories.title) LIKE '%term1%' OR (stories.description) LIKE '%term1%' OR (stories.story_cache) LIKE '%term1%')) AND `tags`.`tag` = 'tag1' GROUP BY stories.id HAVING (COUNT(stories.id) = 1)