SELECT stories.*, (stories.title) LIKE '%multitag%', (stories.description) LIKE '%multitag%', (stories.story_cache) LIKE '%multitag%' FROM `stories` WHERE `stories`.`merged_story_id` IS NULL AND `stories`.`is_expired` = FALSE AND (((stories.title) LIKE '%multitag%' OR (stories.description) LIKE '%multitag%' OR (stories.story_cache) LIKE '%multitag%')) ORDER BY (((stories.title) LIKE '%multitag%') * 2) DESC, (((stories.description) LIKE '%multitag%') * 1.5) DESC, ((stories.story_cache) LIKE '%multitag%') DESC LIMIT 20 OFFSET 0