SELECT stories.*, (stories.title) LIKE '%term1%', (stories.description) LIKE '%term1%', (stories.story_cache) LIKE '%term1%' FROM `stories` INNER JOIN `domains` ON `domains`.`id` = `stories`.`domain_id` WHERE `stories`.`merged_story_id` IS NULL AND `stories`.`is_expired` = FALSE AND (domains.domain = 'lobste.rs') AND (((stories.title) LIKE '%term1%' OR (stories.description) LIKE '%term1%' OR (stories.story_cache) LIKE '%term1%')) ORDER BY (((stories.title) LIKE '%term1%') * 2) DESC, (((stories.description) LIKE '%term1%') * 1.5) DESC, ((stories.story_cache) LIKE '%term1%') DESC LIMIT 20 OFFSET 0