SELECT stories.*, (stories.title) LIKE '%unique%', (stories.description) LIKE '%unique%', (stories.story_cache) LIKE '%unique%' FROM `stories` WHERE `stories`.`merged_story_id` IS NULL AND `stories`.`is_expired` = FALSE AND (((stories.title) LIKE '%unique%' OR (stories.description) LIKE '%unique%' OR (stories.story_cache) LIKE '%unique%')) ORDER BY (((stories.title) LIKE '%unique%') * 2) DESC, (((stories.description) LIKE '%unique%') * 1.5) DESC, ((stories.story_cache) LIKE '%unique%') DESC LIMIT 20 OFFSET 0