SELECT comments.*, (comment) LIKE '%comment3 comment4%' AS rel_comment, `comments`.`id` AS t0_r0, `comments`.`created_at` AS t0_r1, `comments`.`updated_at` AS t0_r2, `comments`.`short_id` AS t0_r3, `comments`.`story_id` AS t0_r4, `comments`.`user_id` AS t0_r5, `comments`.`parent_comment_id` AS t0_r6, `comments`.`thread_id` AS t0_r7, `comments`.`comment` AS t0_r8, `comments`.`upvotes` AS t0_r9, `comments`.`downvotes` AS t0_r10, `comments`.`confidence` AS t0_r11, `comments`.`markeddown_comment` AS t0_r12, `comments`.`is_deleted` AS t0_r13, `comments`.`is_moderated` AS t0_r14, `comments`.`is_from_email` AS t0_r15, `comments`.`hat_id` AS t0_r16, `users`.`id` AS t1_r0, `users`.`username` AS t1_r1, `users`.`email` AS t1_r2, `users`.`password_digest` AS t1_r3, `users`.`created_at` AS t1_r4, `users`.`is_admin` AS t1_r5, `users`.`password_reset_token` AS t1_r6, `users`.`session_token` AS t1_r7, `users`.`about` AS t1_r8, `users`.`invited_by_user_id` AS t1_r9, `users`.`is_moderator` AS t1_r10, `users`.`pushover_mentions` AS t1_r11, `users`.`rss_token` AS t1_r12, `users`.`mailing_list_token` AS t1_r13, `users`.`mailing_list_mode` AS t1_r14, `users`.`karma` AS t1_r15, `users`.`banned_at` AS t1_r16, `users`.`banned_by_user_id` AS t1_r17, `users`.`banned_reason` AS t1_r18, `users`.`deleted_at` AS t1_r19, `users`.`disabled_invite_at` AS t1_r20, `users`.`disabled_invite_by_user_id` AS t1_r21, `users`.`disabled_invite_reason` AS t1_r22, `users`.`settings` AS t1_r23, `stories`.`id` AS t2_r0, `stories`.`created_at` AS t2_r1, `stories`.`user_id` AS t2_r2, `stories`.`url` AS t2_r3, `stories`.`title` AS t2_r4, `stories`.`description` AS t2_r5, `stories`.`short_id` AS t2_r6, `stories`.`is_expired` AS t2_r7, `stories`.`upvotes` AS t2_r8, `stories`.`downvotes` AS t2_r9, `stories`.`is_moderated` AS t2_r10, `stories`.`hotness` AS t2_r11, `stories`.`markeddown_description` AS t2_r12, `stories`.`story_cache` AS t2_r13, `stories`.`comments_count` AS t2_r14, `stories`.`merged_story_id` AS t2_r15, `stories`.`unavailable_at` AS t2_r16, `stories`.`twitter_id` AS t2_r17, `stories`.`user_is_author` AS t2_r18, `stories`.`user_is_following` AS t2_r19, `stories`.`domain_id` AS t2_r20 FROM `comments` INNER JOIN `stories` ON `stories`.`id` = `comments`.`story_id` INNER JOIN `domains` ON `domains`.`id` = `stories`.`domain_id` LEFT OUTER JOIN `users` ON `users`.`id` = `comments`.`user_id` WHERE `comments`.`is_deleted` = FALSE AND `comments`.`is_moderated` = FALSE AND (domains.domain = 'lobste.rs') AND ((comment) LIKE '%comment3 comment4%') ORDER BY rel_comment DESC LIMIT 20 OFFSET 0