SELECT COUNT(DISTINCT comments.id) FROM `comments` INNER JOIN `stories` ON `stories`.`id` = `comments`.`story_id` INNER JOIN `domains` ON `domains`.`id` = `stories`.`domain_id` LEFT OUTER JOIN `users` ON `users`.`id` = `comments`.`user_id` WHERE `comments`.`is_deleted` = FALSE AND `comments`.`is_moderated` = FALSE AND (domains.domain = 'lobste.rs') AND ((comment) LIKE '%comment3 comment4%')