SELECT DISTINCT `documents`.`created_on`, `documents`.`id` FROM `documents` INNER JOIN `projects` ON `projects`.`id` = `documents`.`project_id` WHERE (((projects.status <> 9 AND EXISTS (SELECT 1 AS one FROM enabled_modules em WHERE em.project_id = projects.id AND em.name='documents')) AND ((projects.is_public = TRUE AND projects.id NOT IN (SELECT project_id FROM members WHERE user_id IN (6,13)))))) AND (((title LIKE '%bye bye%') OR (documents.description LIKE '%bye bye%')) OR ((title LIKE '%good bye%') OR (documents.description LIKE '%good bye%')) OR ((title LIKE '%hello%') OR (documents.description LIKE '%hello%'))) ORDER BY `documents`.`created_on` DESC, `documents`.`id` DESC