SELECT DISTINCT `messages`.`created_on`, `messages`.`id` FROM `messages` INNER JOIN `boards` ON `boards`.`id` = `messages`.`board_id` INNER JOIN `projects` ON `projects`.`id` = `boards`.`project_id` WHERE (((projects.status <> 9 AND EXISTS (SELECT 1 AS one FROM enabled_modules em WHERE em.project_id = projects.id AND em.name='boards')) AND ((projects.is_public = TRUE AND projects.id NOT IN (SELECT project_id FROM members WHERE user_id IN (6,13)))))) AND (((subject LIKE '%bye bye%') OR (content LIKE '%bye bye%')) OR ((subject LIKE '%good bye%') OR (content LIKE '%good bye%')) OR ((subject LIKE '%hello%') OR (content LIKE '%hello%'))) ORDER BY `messages`.`created_on` DESC, `messages`.`id` DESC