SELECT DISTINCT `projects`.`created_on`, `projects`.`id` FROM `projects` WHERE (((projects.status <> 9) AND ((projects.is_public = TRUE AND projects.id NOT IN (SELECT project_id FROM members WHERE user_id IN (6,13)))))) AND (((name LIKE '%bye bye%') OR (identifier LIKE '%bye bye%') OR (description LIKE '%bye bye%')) OR ((name LIKE '%good bye%') OR (identifier LIKE '%good bye%') OR (description LIKE '%good bye%')) OR ((name LIKE '%hello%') OR (identifier LIKE '%hello%') OR (description LIKE '%hello%'))) ORDER BY `projects`.`created_on` DESC, `projects`.`id` DESC