SELECT DISTINCT `projects`.`created_on`, `projects`.`id` FROM `projects` WHERE (((projects.status <> 9) AND ((projects.is_public = TRUE AND projects.id NOT IN (SELECT project_id FROM members WHERE user_id IN (6,13)))))) AND (((name LIKE '%highlighted%') OR (identifier LIKE '%highlighted%') OR (description LIKE '%highlighted%'))) ORDER BY `projects`.`created_on` DESC, `projects`.`id` DESC