SELECT `users`.* FROM `users` WHERE `users`.`status` = 1 AND `users`.`status` = 1 AND `users`.`type` IN ('User', 'Group') AND (LOWER(users.login) LIKE LOWER('%issue15622%') OR users.id IN (SELECT user_id FROM email_addresses WHERE LOWER(address) LIKE LOWER('%issue15622%')) OR ((LOWER(users.firstname) LIKE LOWER('%issue15622%') OR LOWER(users.lastname) LIKE LOWER('%issue15622%')))) ORDER BY users.type DESC, users.firstname, users.lastname, users.id LIMIT 100