SELECT 1 AS one FROM `projects` WHERE (((projects.status = 1 AND EXISTS (SELECT 1 AS one FROM enabled_modules em WHERE em.project_id = projects.id AND em.name='issue_tracking')) AND (projects.id IN (1,5) OR projects.id IN (2)))) AND (projects.id IN (SELECT DISTINCT project_id FROM projects_trackers)) AND `projects`.`id` = 3 LIMIT 1