SELECT DISTINCT `documents`.`created_on`, `documents`.`id` FROM `documents` INNER JOIN `projects` ON `projects`.`id` = `documents`.`project_id` WHERE (projects.status <> 9 AND EXISTS (SELECT 1 AS one FROM enabled_modules em WHERE em.project_id = projects.id AND em.name='documents')) AND (documents.project_id IN (1,5,6,3,4)) AND (((title LIKE '%Issue%') OR (documents.description LIKE '%Issue%')) AND ((title LIKE '%of%') OR (documents.description LIKE '%of%')) AND ((title LIKE '%private%') OR (documents.description LIKE '%private%')) AND ((title LIKE '%subproject%') OR (documents.description LIKE '%subproject%'))) ORDER BY `documents`.`created_on` DESC, `documents`.`id` DESC