SELECT DISTINCT `changesets`.`committed_on`, `changesets`.`id` FROM `changesets` INNER JOIN `repositories` ON `repositories`.`id` = `changesets`.`repository_id` INNER JOIN `projects` ON `projects`.`id` = `repositories`.`project_id` WHERE (projects.status <> 9 AND EXISTS (SELECT 1 AS one FROM enabled_modules em WHERE em.project_id = projects.id AND em.name='repository')) AND (repositories.project_id IN (1,5,6,3,4)) AND (((comments LIKE '%Issue%')) AND ((comments LIKE '%of%')) AND ((comments LIKE '%private%')) AND ((comments LIKE '%subproject%'))) ORDER BY `changesets`.`committed_on` DESC, `changesets`.`id` DESC