SELECT DISTINCT `wiki_pages`.`created_on`, `wiki_pages`.`id` FROM `wiki_pages` INNER JOIN `wiki_contents` ON `wiki_contents`.`page_id` = `wiki_pages`.`id` INNER JOIN `wikis` ON `wikis`.`id` = `wiki_pages`.`wiki_id` INNER JOIN `projects` ON `projects`.`id` = `wikis`.`project_id` WHERE (projects.status <> 9 AND EXISTS (SELECT 1 AS one FROM enabled_modules em WHERE em.project_id = projects.id AND em.name='wiki')) AND (wikis.project_id IN (1,5,6,3,4)) AND (((title LIKE '%Issue%') OR (wiki_contents.text LIKE '%Issue%')) AND ((title LIKE '%of%') OR (wiki_contents.text LIKE '%of%')) AND ((title LIKE '%private%') OR (wiki_contents.text LIKE '%private%')) AND ((title LIKE '%subproject%') OR (wiki_contents.text LIKE '%subproject%'))) ORDER BY `wiki_pages`.`created_on` DESC, `wiki_pages`.`id` DESC