SELECT DISTINCT `messages`.`created_on`, `messages`.`id` FROM `messages` INNER JOIN `boards` ON `boards`.`id` = `messages`.`board_id` INNER JOIN `projects` ON `projects`.`id` = `boards`.`project_id` WHERE (projects.status <> 9 AND EXISTS (SELECT 1 AS one FROM enabled_modules em WHERE em.project_id = projects.id AND em.name='boards')) AND (boards.project_id IN (1,5,6,3,4)) AND (((subject LIKE '%Issue%') OR (content LIKE '%Issue%')) AND ((subject LIKE '%of%') OR (content LIKE '%of%')) AND ((subject LIKE '%private%') OR (content LIKE '%private%')) AND ((subject LIKE '%subproject%') OR (content LIKE '%subproject%'))) ORDER BY `messages`.`created_on` DESC, `messages`.`id` DESC