SELECT DISTINCT `news`.`created_on`, `news`.`id` FROM `news` INNER JOIN `projects` ON `projects`.`id` = `news`.`project_id` WHERE (projects.status <> 9 AND EXISTS (SELECT 1 AS one FROM enabled_modules em WHERE em.project_id = projects.id AND em.name='news')) AND (((title LIKE '%Issue%') OR (summary LIKE '%Issue%') OR (news.description LIKE '%Issue%')) AND ((title LIKE '%of%') OR (summary LIKE '%of%') OR (news.description LIKE '%of%')) AND ((title LIKE '%private%') OR (summary LIKE '%private%') OR (news.description LIKE '%private%')) AND ((title LIKE '%subproject%') OR (summary LIKE '%subproject%') OR (news.description LIKE '%subproject%'))) ORDER BY `news`.`created_on` DESC, `news`.`id` DESC