SELECT DISTINCT `projects`.`created_on`, `projects`.`id` FROM `projects` WHERE (projects.status <> 9) AND (((name LIKE '%Issue%') OR (identifier LIKE '%Issue%') OR (description LIKE '%Issue%')) AND ((name LIKE '%of%') OR (identifier LIKE '%of%') OR (description LIKE '%of%')) AND ((name LIKE '%private%') OR (identifier LIKE '%private%') OR (description LIKE '%private%')) AND ((name LIKE '%subproject%') OR (identifier LIKE '%subproject%') OR (description LIKE '%subproject%'))) ORDER BY `projects`.`created_on` DESC, `projects`.`id` DESC