SELECT COUNT(*) FROM `news` INNER JOIN `projects` ON `projects`.`id` = `news`.`project_id` WHERE `news`.`project_id` = 1 AND (((projects.status <> 9 AND EXISTS (SELECT 1 AS one FROM enabled_modules em WHERE em.project_id = projects.id AND em.name='news')) AND ((projects.is_public = TRUE AND projects.id NOT IN (SELECT project_id FROM members WHERE user_id IN (6,13))))))