SELECT wiki_content_versions.updated_on, wiki_content_versions.comments, wiki_content_versions.version, wiki_pages.title, wiki_content_versions.page_id, wiki_content_versions.author_id, wiki_content_versions.id FROM `wiki_content_versions` LEFT JOIN wiki_pages ON wiki_pages.id = wiki_content_versions.page_id LEFT JOIN wikis ON wikis.id = wiki_pages.wiki_id LEFT JOIN projects ON projects.id = wikis.project_id WHERE (wiki_content_versions.updated_on BETWEEN '2020-04-16' AND '2020-05-16') AND (wiki_content_versions.author_id = 2) AND (((projects.status <> 9 AND EXISTS (SELECT 1 AS one FROM enabled_modules em WHERE em.project_id = projects.id AND em.name='wiki')) AND ((projects.is_public = TRUE AND projects.id NOT IN (SELECT project_id FROM members WHERE user_id IN (6,13))))))