SELECT `issues`.* FROM `issues` INNER JOIN `projects` ON `projects`.`id` = `issues`.`project_id` WHERE (issues.author_id = 4) AND (((projects.status <> 9 AND EXISTS (SELECT 1 AS one FROM enabled_modules em WHERE em.project_id = projects.id AND em.name='issue_tracking')) AND (((projects.is_public = TRUE AND projects.id NOT IN (SELECT project_id FROM members WHERE user_id IN (4,12))) AND ((issues.is_private = FALSE OR issues.author_id = 4 OR issues.assigned_to_id IN (4))))))) ORDER BY issues.id DESC LIMIT 10