SELECT `time_entries`.* FROM `time_entries` INNER JOIN `projects` ON `projects`.`id` = `time_entries`.`project_id` WHERE (time_entries.created_on BETWEEN '2020-04-16' AND '2020-05-16') AND (time_entries.user_id = 2) AND (projects.status <> 9 AND EXISTS (SELECT 1 AS one FROM enabled_modules em WHERE em.project_id = projects.id AND em.name='time_tracking'))