SELECT DISTINCT `issues`.`created_on`, `issues`.`id` FROM `issues` INNER JOIN `projects` ON `projects`.`id` = `issues`.`project_id` WHERE (projects.status <> 9 AND EXISTS (SELECT 1 AS one FROM enabled_modules em WHERE em.project_id = projects.id AND em.name='issue_tracking')) AND (((subject LIKE '%value0%') OR (issues.description LIKE '%value0%'))) ORDER BY `issues`.`created_on` DESC, `issues`.`id` DESC