SELECT DISTINCT `issues`.`created_on`, `issues`.`id` FROM `issues` INNER JOIN `projects` ON `projects`.`id` = `issues`.`project_id` INNER JOIN `journals` ON `journals`.`journalized_id` = `issues`.`id` AND `journals`.`journalized_type` = 'Issue' WHERE (projects.status <> 9 AND EXISTS (SELECT 1 AS one FROM enabled_modules em WHERE em.project_id = projects.id AND em.name='issue_tracking')) AND (journals.private_notes = FALSE OR (projects.status <> 9 AND EXISTS (SELECT 1 AS one FROM enabled_modules em WHERE em.project_id = projects.id AND em.name='issue_tracking'))) AND (((journals.notes LIKE '%value0%'))) ORDER BY `issues`.`created_on` DESC, `issues`.`id` DESC