SELECT DISTINCT `news`.`created_on`, `news`.`id` FROM `news` INNER JOIN `projects` ON `projects`.`id` = `news`.`project_id` WHERE (projects.status <> 9 AND EXISTS (SELECT 1 AS one FROM enabled_modules em WHERE em.project_id = projects.id AND em.name='news')) AND (((title LIKE '%value0%') OR (summary LIKE '%value0%') OR (news.description LIKE '%value0%'))) ORDER BY `news`.`created_on` DESC, `news`.`id` DESC