SELECT DISTINCT `wiki_pages`.`created_on`, `wiki_pages`.`id` FROM `wiki_pages` INNER JOIN `wiki_contents` ON `wiki_contents`.`page_id` = `wiki_pages`.`id` INNER JOIN `wikis` ON `wikis`.`id` = `wiki_pages`.`wiki_id` INNER JOIN `projects` ON `projects`.`id` = `wikis`.`project_id` WHERE (projects.status <> 9 AND EXISTS (SELECT 1 AS one FROM enabled_modules em WHERE em.project_id = projects.id AND em.name='wiki')) AND (((title LIKE '%value0%') OR (wiki_contents.text LIKE '%value0%'))) ORDER BY `wiki_pages`.`created_on` DESC, `wiki_pages`.`id` DESC