SELECT DISTINCT `messages`.`created_on`, `messages`.`id` FROM `messages` INNER JOIN `boards` ON `boards`.`id` = `messages`.`board_id` INNER JOIN `projects` ON `projects`.`id` = `boards`.`project_id` WHERE (projects.status <> 9 AND EXISTS (SELECT 1 AS one FROM enabled_modules em WHERE em.project_id = projects.id AND em.name='boards')) AND (((subject LIKE '%value0%') OR (content LIKE '%value0%'))) ORDER BY `messages`.`created_on` DESC, `messages`.`id` DESC