SELECT DISTINCT `issues`.`created_on`, `issues`.`id` FROM `issues` INNER JOIN `projects` ON `projects`.`id` = `issues`.`project_id` INNER JOIN `journals` ON `journals`.`journalized_id` = `issues`.`id` AND `journals`.`journalized_type` = 'Issue' WHERE (((projects.status <> 9 AND EXISTS (SELECT 1 AS one FROM enabled_modules em WHERE em.project_id = projects.id AND em.name='issue_tracking')) AND (((projects.is_public = TRUE AND projects.id NOT IN (SELECT project_id FROM members WHERE user_id IN (3,12))) AND ((issues.is_private = FALSE OR issues.author_id = 3 OR issues.assigned_to_id IN (3)))) OR (projects.id IN (1) AND ((issues.is_private = FALSE OR issues.author_id = 3 OR issues.assigned_to_id IN (3))))))) AND (journals.private_notes = FALSE OR (1=0)) AND (((journals.notes LIKE '%value0%'))) ORDER BY `issues`.`created_on` DESC, `issues`.`id` DESC