SELECT DISTINCT `projects`.`created_on`, `projects`.`id` FROM `projects` WHERE (((projects.status <> 9) AND ((projects.is_public = TRUE AND projects.id NOT IN (SELECT project_id FROM members WHERE user_id IN (3,12))) OR projects.id IN (1)))) AND (((name LIKE '%value0%') OR (identifier LIKE '%value0%') OR (description LIKE '%value0%'))) ORDER BY `projects`.`created_on` DESC, `projects`.`id` DESC