SELECT DISTINCT `news`.`created_on`, `news`.`id` FROM `news` INNER JOIN `projects` ON `projects`.`id` = `news`.`project_id` WHERE (((projects.status <> 9 AND EXISTS (SELECT 1 AS one FROM enabled_modules em WHERE em.project_id = projects.id AND em.name='news')) AND ((projects.is_public = TRUE AND projects.id NOT IN (SELECT project_id FROM members WHERE user_id IN (6,13)))))) AND (news.project_id IN (1)) AND (((title LIKE '%recipe%') OR (summary LIKE '%recipe%') OR (news.description LIKE '%recipe%')) AND ((title LIKE '%saving%') OR (summary LIKE '%saving%') OR (news.description LIKE '%saving%')) AND ((title LIKE '%updating%') OR (summary LIKE '%updating%') OR (news.description LIKE '%updating%'))) ORDER BY `news`.`created_on` DESC, `news`.`id` DESC