SELECT DISTINCT `issues`.`created_on`, `issues`.`id` FROM `issues` INNER JOIN `issue_statuses` ON `issue_statuses`.`id` = `issues`.`status_id` INNER JOIN `projects` ON `projects`.`id` = `issues`.`project_id` WHERE `issue_statuses`.`is_closed` = FALSE AND (((projects.status <> 9 AND EXISTS (SELECT 1 AS one FROM enabled_modules em WHERE em.project_id = projects.id AND em.name='issue_tracking')) AND (((projects.is_public = TRUE AND projects.id NOT IN (SELECT project_id FROM members WHERE user_id IN (6,13))) AND ((issues.is_private = FALSE)))))) AND (issues.project_id IN (1)) AND (((subject LIKE '%search_open%') OR (issues.description LIKE '%search_open%'))) ORDER BY `issues`.`created_on` DESC, `issues`.`id` DESC