SELECT DISTINCT `issues`.`created_on`, `issues`.`id` FROM `issues` INNER JOIN `projects` ON `projects`.`id` = `issues`.`project_id` INNER JOIN `attachments` ON `attachments`.`container_id` = `issues`.`id` AND `attachments`.`container_type` = 'Issue' WHERE (((projects.status <> 9 AND EXISTS (SELECT 1 AS one FROM enabled_modules em WHERE em.project_id = projects.id AND em.name='issue_tracking')) AND (((projects.is_public = TRUE AND projects.id NOT IN (SELECT project_id FROM members WHERE user_id IN (6,13))) AND ((issues.is_private = FALSE)))))) AND (issues.project_id IN (1)) AND (((attachments.filename LIKE '%search_attachments%') OR (attachments.description LIKE '%search_attachments%'))) ORDER BY `issues`.`created_on` DESC, `issues`.`id` DESC